// based on this tutorial: https://www.youtube.com/watch?v=8--hS-PhRz8

#define PI 3.14159265359

float def(vec2 uv, float f) {

    float n = 10.; 
    float e = 1.;

    for(float i=0.; i < n; i++) {
        // center
        vec2 p = vec2(.5, i/n) - uv;
        // radius
        float rad = length(p)*1.;
        // angle
        float ang = atan(p.x, p.y);

        e += sin(rad*15.+f+iTime+abs(sin(rad*PI)));
        e += sin(rad+iTime+sin(rad*PI*10.));
        e += sin(e*PI)*.1;
    }

    e /= n/3.;

    return abs(e);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	// center
    vec2 p = vec2(.5) - uv;
    // radius
    float rad = length(p)*1.;
    // angle
    float ang = atan(p.x, p.y);

    // shape
    float e = def(uv, 0.);
    float e2 = def(uv, PI/6.);

    // colors
    vec4 c1 = vec4(.6, .3, .9, 1.);
    vec4 c2 = vec4(.1, .5, .8, 1.);

    // final color
    vec4 color = vec4(e)*c1*c1.a+vec4(e2)*c2*c2.a;

    fragColor = color;

}
