// Author: hucancode
// Title: arc motion
// open with: https://thebookofshaders.com/edit.php
#define PI2 6.28 
#define PI 3.14
#define ARC_RADIUS 0.7
#define ARC_WIDTH 0.02
#define ARC_LENGTH PI*0.5
#define SMOOTHNESS 0.5
// radian per second
#define SPEED 1.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord.xy/iResolution.xy;
    //uv.x *= iResolution.x/iResolution.y;
    //uv = uv*2.0-vec2(1.0);
    vec2 uv = ( 2.0*fragCoord -iResolution.xy ) / iResolution.y;

    float angle_offset = mod(iTime*SPEED,PI2)-PI;
    float r = uv.x*uv.x +uv.y*uv.y;
    float angle = uv.x==0.0?0.0:atan(uv.y,uv.x);
    float delta_angle = angle_offset - angle;
    delta_angle = mod(delta_angle+PI2,PI2);

    float circle = smoothstep(ARC_WIDTH,0.0,abs(r-ARC_RADIUS));
    float fan = smoothstep(ARC_LENGTH,ARC_LENGTH-SMOOTHNESS*ARC_LENGTH, abs(delta_angle-ARC_LENGTH));
    float value = circle*fan;

    vec3 color = vec3(value);
    fragColor = vec4(color,1.0);
}
