void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;	// Scale pixel coords to 0..1 range instead
    //vec2 uv = vec2(fragCoord.x, fragCoord.y/iResolution.y);

    float pi2 = 3.1415926535 * 2.;
    float waves = 30.;
    float speed = 20.;

    float height= 140.;

    vec3 c = vec3(0.,0.,0.);
    float dx = -0.4*iTime;



    float y = sin((uv.x + dx) * pi2 * waves);// * cos(0.0) + (iTime * speed));
	//float y_max = (uv.y-0.5) * height /* * (0.1*sin(iTime))*/;

    height *= (1.0 + 0.5*sin(20.0*uv.x));
    float y_max = (uv.y - 0.5) * height * (1.0 + 0.2*sin(iTime * speed)) ;
    float y_min = y_max - 1.1;

    if(y < y_max && y > y_min){
        c = vec3(1.0,0.0,0.2);
    }

	fragColor   = vec4(c,1.);
}
