void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
		vec2 uv = ( fragCoord -.5* iResolution.xy ) / iResolution.y;

		uv.x -= sin(iTime)*.4;

		float d = length(uv);
		float r = .2 * abs((cos(iTime)+1.)/2.)+0.1;

		float c = smoothstep(r, r-(1./iResolution.y), d);

		fragColor = vec4( c );
}
