float band(float t, float start, float end, float blur)
{
 	float step1 = smoothstep(start-blur, start+blur, t);

    float step2 = smoothstep(end+blur, end-blur, t);

    return step1 * step2;
}

float rect(vec2 uv, float left, float right, float bottom, float top, float blur) 
{
    float band1 = band(uv.x, left, right, blur);
    float band2 = band(uv.y, bottom, top, blur);

    return band1 * band2;
}

float Circle(vec2 uv, vec2 p, float r, float blur) 
{
 	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float smiley(vec2 uv, vec2 p, float size) 
{
    uv -= p;
    uv /= size;

	float c = Circle(uv, vec2(0), .4, .05);

    //c += Circle(uv, vec2(-.4, -.2), .1, .01);
    c -= Circle(uv, vec2(.2, .1), .05, .01);
    c -= Circle(uv, vec2(-.2, .1), .05, .01);

    float crescent = Circle(uv, vec2(0., 0.05), .3, 0.05);
    crescent -= Circle(uv, vec2(0), .3, 0.05);

    c += crescent;

    return c;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float crescent = smiley(uv, vec2(-.5,0), .5);

    crescent -= smiley(uv, vec2(.5,0), .7);
    crescent -= smiley(uv, vec2(.05,0.2), .1);

    uv *= cos(iTime)+uv.x;
    float blur = length(uv)+max(sin(iTime), 0.0);
    crescent += rect(uv, -1., 0.8, -0.2, 0.2, blur);
    col = vec3(crescent);

    // Output to screen
    fragColor = vec4(col, 1.0);

}
