#define pi 3.1415926
float sdfbox(vec2 p,vec2 size)
{
    return length(max(abs(p)-size,0.0));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/ iResolution.y;
   	vec2 p=uv;
    vec3 col;
    float a = atan(p.y,p.x)/pi;
    float ttime=0.;
    ttime=mod(ttime+iTime*2.2,2.);
    float r = length(p);
    p = vec2(r,a);
 	p = (1.0 * p) - vec2(0.0,0.122);
    p.xy=p.yx;
    float tt=0.014;
    float yy=0.092;
    float d;
    float t=1.0;
    float t1=1.0;
    for(int i=1;i<=5;i++)
    {
        d=smoothstep(0.004,0.01,sdfbox(p-vec2(t,yy),vec2(0.104,tt)));
       	if(ttime>t1&&ttime<t1+0.3) col=mix(col,vec3(.7),1.-d);
        else col=mix(col,vec3(0.1),1.-d);
        t-=.25;
        t1+=.25;
    }
    t=0.0;
    for(int i=1;i<=5;i++)
    {
        d=smoothstep(0.004,0.01,sdfbox(p-vec2(-t,yy),vec2(0.104,tt)));
        if(ttime>t&&ttime<t+0.3) col=mix(col,vec3(.7),1.-d);
        else col=mix(col,vec3(0.1),1.-d);
        t+=.25;
    }
    // Output to screen
    col = pow(col, vec3(1.0 / 2.2));
    fragColor = vec4(col,1.0);
}
