float speed = 10.0;
float thickness = 0.1;
float spread = 10.0;
float direction = 1.0; // 1.0 OR -1.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord.xy;
    vec2 center = iResolution.xy / 2.0;

    float dist = length(center - coord);
    float distToEdge = 1.0 - dist / (iResolution.x / 2.0);
    float x = center.x - coord.x;
    float y = center.y - coord.y;
    float circles = cos(dist / spread - (iTime * direction) * speed);

    circles = smoothstep(1.0 - (thickness / spread), 1.0, circles);

    circles *= thickness * 10.0;

    circles = clamp(0.0, 1.0, circles);


    float c = circles;

    // fade out towards the edge
    c *= distToEdge;

	fragColor = vec4(c, c, c, 1.0);
}
