void mainImage( out vec4 O, vec2 U )
{
    vec2  R = iResolution.xy;
    float t = mod(iTime/5.,2.),
          n = log2(1.+min(t,2.-t));
    O = texture(iChannel0, fract( (U/R-.5)/n+.5 ));
}

