renderpass.code:
float rand(vec2 p){
    return fract(sin(p.x*102.33+p.y*3623.42)*27827.);
}

float smoothrand(vec2 p){
    vec2 lv = fract(p);
    vec2 id = floor(p);

    lv = lv*lv*(3.-2.*lv);

    float b = mix(rand(id),rand(id+vec2(1.,0.)),lv.x);
	float t = mix(rand(id+vec2(0.,1.)),rand(id+vec2(1.,1.)),lv.x);

    return mix(b,t,lv.y);
}

float noise(vec2 p,uint depth){
    float c=0.;
    float amp=1.;
    float a=0.;
    for(uint i = 0U;i<=depth;i++){
        c+=smoothrand(p+vec2(float(i)))*amp;

        p*=2.*vec2(( (i%2U == 0U)?(-1.):(1.) ),( (i%2U == 0U)?(1.):(-1.) ));
        a+=amp;
        amp/=2.;
    }
    return c / a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float zoom = 1.4;
    uv *= zoom;

    vec3 col;
    vec2 bv = uv*0.5;
    float t = iTime;
    float len = length(bv);
    col = vec3(noise(vec2(bv.x*bv.y,len-t),2U),noise(vec2(bv.x*bv.y+100.,len-t),2U),noise(vec2(bv.x*bv.y-100.,len-t),2U));


    bv = uv;
    bv.x = abs(uv.x) - 1.26;
    bv.y += 0.702;


    len = length(bv);
    float v = 0.94 + noise(bv/len+vec2(150.,100.),2U)*0.06;
    col = mix(vec3(0.),col,smoothstep(v*0.28 - 0.005,v*0.28,len));
    float noisytexture = (1.-.12*noise(uv*3.,10U));
    col = mix(vec3(.97*noisytexture,.77*noisytexture,.62*noisytexture),col,smoothstep(v*0.265-0.005,v*0.265,len));

    bv = uv;

    bv.y += 0.286;
    bv.y *= 1.;
    len = length(bv);
     v = 0.94 + noise(bv/len+vec2(150.,100.),2U)*0.06;
    col = mix(vec3(0.),col,smoothstep(v*1.41 - 0.005,v*1.41,len));
    noisytexture = (1.-.12*noise(uv*3.,10U));
    col = mix(vec3(.97*noisytexture,.77*noisytexture,.62*noisytexture),col,smoothstep(v*1.395-0.005,v*1.395,len));



    float xsign = sign(uv.x);
    uv.x = abs(uv.x) - 0.629;

    len = length(uv);
    v = 0.92 + noise((uv/len+vec2(100.+t,143.-t)*0.1)*1.,3U)*0.08;
    col = mix(vec3(0.),col,smoothstep(v*0.5 - 0.005,v*0.5,len));
    col = mix(vec3(1.),col,smoothstep(v*0.485-0.005,v*0.485,len));



    bv = uv;
    float r = noise(vec2(t*1.),3U);
    float sr = smoothrand(vec2(t));
    bv -= vec2((.24*sin(r*6.28)*sr)*xsign,.2*cos(r*6.28)*sr);
    len = length(bv);
	v = 0.2 + noise((bv/len+vec2(t*.6))*1.,3U)*0.8;
    col = mix(vec3(0.),col,smoothstep(v*0.1-0.005,v*0.1,len));

    // Output to screen
    fragColor = vec4(col,1.0);
}
