void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    float ANGLE = .2;
    float SIZE = 1.7;
    float BLUR = .9;

    vec2 p = (2. * fragCoord.xy - iResolution.xy) / iResolution.y * 2.;

    float d = max(abs(p.x)+ ANGLE * p.y, abs(p.y)) - SIZE;
    float t = 1. - smoothstep(BLUR * .1, BLUR,d);

    // Gradient to fade out

    fragColor = vec4(t);
}
