#define PI 3.1415926538
float sfloor( float v, int n){
    float o = v;
    for(int i = 0; i<n; i++){
    	o = o - sin(2.*PI*o)/(2.*PI);
    }
    return o;
}
float getVal(float x0,float y0,float inter,float m){
    float x = cos(x0)*0.5 + 0.5;
    float y = sin(y0)*0.5 + 0.5;

    float t = mix(x,y,inter);
    //t =  t;
    //float val = floor(t*m)/m;
    float val = clamp(sfloor(t*(m-0.5)-0.25,3),0.,m-1.)/(m-1.);
    return val;
}

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

