float pattern_dimond(vec2 uv , vec2 center , float radius)
{
	float distance = dot(abs(uv-center), vec2(1.0));
    float aaf = fwidth(distance);// 1 pixel width, factor distnace.xy to be anti-aliasing factor
    float mask = smoothstep(radius-aaf,radius,distance);
    return 1.-mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy) /min(iResolution.x,iResolution.y);

    float mask = pattern_dimond(uv,vec2(0.2,0.3),0.5);

    // Output to screen
    fragColor = vec4(vec3(mask),1.);
}
