float circleSdf(vec2 p, vec2 center, float r) {
  return length(p - center) - r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  const vec3 red = vec3(242.0 / 255.0, 25.0 / 255.0, 5.0 / 255.0);
  const vec3 blue = vec3(44.0 / 255.0, 54.0 / 255.0, 115.0 / 255.0);
  const vec3 gold = vec3(217.0 / 255.0, 147.0 / 255.0, 17.0 / 255.0);

  vec2 st = fragCoord / iResolution.x;
  vec3 col = red;

  vec2 center = vec2(0.5, 0.285);
  float sdf = circleSdf(st, center, 0.15);
  if (sdf < 0.0) {
    float c = cos(iTime);
    float s = sin(iTime);
    mat2 rot = mat2(c, -s, s, c);
    float threshold = c;
    vec2 unit = normalize(st - center);
    float unitDot = dot(rot * vec2(0,1), unit);
    col = vec3(step(threshold, unitDot)) * blue;
    col = max(col, vec3(step(unitDot, threshold)) * gold);
  }

  fragColor = vec4(col, 1);
}
