vec2 position = vec2(.70,.25);
vec2 position2 = vec2(.30,.25);
vec2 position3 = vec2(.50,.60);

float radius = .2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Fixes scaling of the circles 
    uv.y *= (iResolution.y / iResolution.x);

    //Move third circle up and down
    position3.y *= abs(sin(iTime) * .6);   


    vec4 circle = vec4(1.0,0.0,0.0,1.0); //Red Circle


    vec4 circle2 = vec4(0.0, 1.0, 0.0, 1.0); //Green Circle


    vec4 circle3 = vec4(0.0, 0.0, 1.0, 1.0); //Blue Circle 


    //Defines all 3 circles
    circle *= step(length(uv - position), radius); 
    circle2 *= step(length(uv - position2), radius);
    circle3 *= step(length(uv - position3), radius);


    //Blending the circles together
    vec4 composite = mix(circle, circle3, circle3.a);
    composite = mix(composite, circle2, circle2.a);


    //Display
    fragColor = composite;
}
