void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    fragColor=vec4(0);

    vec4 s=texelFetch(iChannel0,ivec2(0),0);

    vec2 a=-vec2(sin(s.x),cos(s.x))*l1;
    vec2 b=-vec2(sin(s.y),cos(s.y))*l2;

    vec2 p1=origin+a;
    vec2 p2=p1+b;

    fragColor+=line(uv,origin,p1,0.01);
    fragColor+=line(uv,p1,p2,0.01);

    p1-=uv;
    p2-=uv;

    fragColor.rgb+=vec3(0,0,1)*0.001*m1/dot(p1,p1);
    fragColor.rgb+=vec3(1,0,0)*0.001*m2/dot(p2,p2);


    fragColor+=
         pow(texelFetch(iChannel1,ivec2(fragCoord), 0),vec4(1./1.8));
}

#define tf texelFetch
const float pi=4.*atan(1.);


const float m1=	2.;
const float m2= 1.;

const float l1=	0.3;
const float l2=	0.6;

const float g=	9.8;

const float dt=	1./60.;

const float damping=0.998;	//0.5**(1/60/(vida media en segundos))

const vec2 origin=vec2(0);

const vec4 initial_state=vec4(
    pi*.75		,
    -pi			,
    0			,
    0
);

/*const vec4 initial_state=vec4(
    pi*.25		,
    -pi*.375	,
    0			,
    0
);*/




const float line_smoothness=0.01;
float line(vec2 uv, vec2 p, vec2 p2, float width){
	vec2 n=uv-p;
	vec2 w=p2-p;
	float r=clamp(dot(n,w)/dot(w,w),0.,1.);
	r=length(n-w*r);
    return clamp(
        smoothstep(
    		width+line_smoothness,
            width,
            r
        ),
        0.,
        1.
    );
}

#define FORCE 1
bool keyPress(int ascii) { return (texture(iChannel1,vec2((.5+float(ascii))/256.,0.25)).x > 0.); }

vec4 acc(vec4 s){
	vec4 f;
	f.x = s.z*dt;
	f.y = s.w*dt;

    float d=s.x-s.y;
    float c1=cos(d);
    float s1=sin(d);

    float a=2.*m1+m2*(1.-cos(2.*d));

	f.z =-g*(2.*m1+m2)*sin(s.x)-m2*g*sin(s.x-2.*s.y)-2.*s1*m2*(s.w*s.w*l2+s.z*s.z*l1*c1);
	f.z/=l1*a;
    f.z*=dt;

    f.w =2.*s1*(s.z*s.z*l1*(m1+m2)+g*(m1+m2)*cos(s.x)+s.w*s.w*l2*m2*c1);
	f.w/=l2*a;
    f.w*=dt;

    return f;
}

void RK4(inout vec4 s){
	vec4 k1 = acc(s			);
	vec4 k2 = acc(s + k1*.5	);
	vec4 k3 = acc(s + k2*.5	);
	vec4 k4 = acc(s + k3	);
    vec4 d=(k1 + k4) / 6. + (k2 + k3) / 3.;
	s += d;
    s.xy = mod(s.xy + pi, 2. * pi) - pi;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    if(fragCoord.x==0.5&&fragCoord.y==0.5){
        vec4 s=texelFetch(iChannel0,ivec2(0),0);
    	RK4(s);
    	//if(iFrame==0)s=-vec4(pi*.75,-pi*.75,0,0);
        if(iFrame==0||keyPress(32))s=initial_state;
        fragColor=s;
    }
    if(fragCoord.x==0.5&&fragCoord.y==1.5)
        fragColor=texelFetch(iChannel0,ivec2(0),0);
}

bool keyPress(int ascii) { return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.25)).x > 0.); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
	fragColor=texelFetch(iChannel1,ivec2(fragCoord),0)*damping;


    vec2 s1=tf(iChannel0,ivec2(0,0),0).rg;	//current time step
    vec2 s0=tf(iChannel0,ivec2(0,1),0).rg;	//one time step back


    vec2 v1=
        -vec2(sin(s1.x),cos(s1.x))*l1
        -vec2(sin(s1.y),cos(s1.y))*l2;		//current time step

    vec2 v0=
        -vec2(sin(s0.x),cos(s0.x))*l1
        -vec2(sin(s0.y),cos(s0.y))*l2;		//one time step back


    vec2 p1=origin+v1;						//current time step
    vec2 p0=origin+v0;						//one time step back

    vec2 k=normalize(p1-p0);
    p0+=k*line_smoothness;

    fragColor+=line(uv,p1,p0,0.);
    if(iFrame==0||keyPress(32))fragColor=vec4(0);

}
