vec4 oldColor;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 texSample 	= texture( iChannel0, uv ).rgb;

    float floor = 0.15f;

    float red = texSample.r - mod(texSample.r, floor);
    float green = texSample.g - mod(texSample.g, floor);
    float blue = texSample.b - mod(texSample.b, floor);

    fragColor = vec4(red, green, blue, 1.0);

}
