#define N 10.0

void mainImage( out vec4 fragColor, in vec2 p )
{
    vec4 Sum = vec4(0.0);
    vec2 d;
    for(d.y = -N; d.y <= +N; d.y++)
    {
        for(d.x = -N; d.x <= +N; d.x++)
        {
            Sum += texture(iChannel0, (p + d) / iResolution.xy);
        }
    }

    vec4 Average = Sum / pow(2.0 * N + 1.0, 2.0);

    //fragColor = Average;
    fragColor = 0.75 * texture(iChannel0, p / iResolution.xy);
    fragColor += 0.75 * Average;
}
