float e = .6;

float loop(vec3 f,vec3 l,vec3 n,float p) {
	l += n;
    f -= n;

    vec3 c = vec3(fract(sin(l.x*3294.42+l.y*91374.42+l.z*281.231)*456.4298))-.5;

    float d = sqrt(pow((f-c).x,e)+pow((f-c).y,e)+pow((f-c).z,e));
    p = min(p,d);
    return p;
}

float voronoi(vec3 uvz, float s) {
	uvz *= s;
    vec3 f = fract(uvz)-.5;
    vec3 l = floor(uvz);
    float p = 10.;
    p = loop(f,l,vec3(0,0,0),p);
    p = loop(f,l,vec3(1,0,0),p);
    p = loop(f,l,vec3(-1,0,0),p);
    p = loop(f,l,vec3(0,1,0),p);
    p = loop(f,l,vec3(1,1,0),p);
    p = loop(f,l,vec3(-1,1,0),p);
    p = loop(f,l,vec3(0,-1,0),p);
    p = loop(f,l,vec3(1,-1,0),p);
    p = loop(f,l,vec3(-1,-1,0),p);
    p = loop(f,l,vec3(0,0,1),p);
    p = loop(f,l,vec3(1,0,1),p);
    p = loop(f,l,vec3(-1,0,1),p);
    p = loop(f,l,vec3(0,1,1),p);
    p = loop(f,l,vec3(1,1,1),p);
    p = loop(f,l,vec3(-1,1,1),p);
    p = loop(f,l,vec3(0,-1,1),p);
    p = loop(f,l,vec3(1,-1,1),p);
    p = loop(f,l,vec3(-1,-1,1),p);
    p = loop(f,l,vec3(0,0,-1),p);
    p = loop(f,l,vec3(1,0,-1),p);
    p = loop(f,l,vec3(-1,0,-1),p);
    p = loop(f,l,vec3(0,1,-1),p);
    p = loop(f,l,vec3(1,1,-1),p);
    p = loop(f,l,vec3(-1,1,-1),p);
    p = loop(f,l,vec3(0,-1,-1),p);
    p = loop(f,l,vec3(1,-1,-1),p);
    p = loop(f,l,vec3(-1,-1,-1),p);

	return p;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 uvz = vec3(uv,iTime/50.);

    vec3 col = vec3(1.-voronoi(uvz,10.));

    fragColor = vec4(col,1.0);
}
