void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates: 0 (bottom left) to 1 (upper right)
    vec2 ouv = fragCoord/iResolution.xy;
    float r = iResolution.x/iResolution.y;

    // me grid
	float g1 = 1./9.;
    float g2 = 2./9.;
    float g3 = 3./9.;
    float g4 = 4./9.;
    float g5 = 5./9.;
    float g6 = 6./9.;
    float g7 = 7./9.;
    float g8 = 8./9.;
    float g9 = 9./9.;
    float a = .205;

    vec2 uv = ouv;
    uv.x *= r;
    uv.x -= g4;

    // rotating vector over time
    vec2 q = .5+.5*vec2(cos(iTime),sin(iTime));

    // color mix
    vec3 col = mix(vec3(1,1,.3),vec3(.8,.1,0),length(ouv-q)); // smoothstep removed

    // mask: mult color by qty one minus zero to one using step() to form letters
    // T
    col *= 1.-(step(g5, uv.y))*(1.-step(g8, uv.y))*(1.-step(g1, uv.x));
    col *= 1.-(step(g2, uv.x))*(step(g5, uv.y))*(1.-step(g3, uv.x))*(1.-step(g8, uv.y));
    // A
    col *= 1.-(step(g3, uv.x))*(step(g5, uv.y))*(1.-step(g2, uv.x-a*uv.y));
    col *= 1.-(1.-step(g6, uv.x))*(step(g5, uv.y))*(step(g7, uv.x+a*uv.y));
    col *= 1.-(step(g3, uv.x))*(step(g6, uv.y))*(1.-step(g6, uv.x+a*uv.y))*(step(g3, uv.x-a*uv.y));
    // L
    col *= 1.-(step(g7, uv.x))*(step(g6, uv.y));

    // gaps
    col *= 1.-(step(g4, uv.y))*(1.-step(g5, uv.y));
    col *= 1.-(step(-1., uv.x))*(1.-step(.01, uv.x))*(1.-step(g4, uv.y));
    col *= 1.-(step(g3, uv.x))*(1.-step(g3+.01, uv.x))*(1.-step(g4, uv.y));

    // F
    col *= 1.-(step(g1, uv.x))*(step(g2, uv.y))*(1.-step(g3, uv.x))*(1.-step(g3, uv.y));
    col *= 1.-(step(g1, uv.x))*(1.-step(g1, uv.y))*(1.-step(g3, uv.x));
    // R
    col *= 1.-(step(g4, uv.x))*(1.-step(g4, uv.y))*(1.-step(g3+g2*abs(cos(.1+uv.y*10.)), uv.x));
    col *= 1.-(step(g5+g1*abs(cos(.1+uv.y*10.)), uv.x))*(1.-step(g6, uv.x))*(1.-step(g4, uv.y));
    // I
    col *= 1.-(step(g6, uv.x))*(1.-step(g7, uv.x))*(1.-step(g5, uv.y));
    col *= 1.-(1.-step(g5, uv.y))*(step(g8, uv.x));

    // Output to screen
    fragColor = vec4(col,1.0);
}
