#define N 5. // try 3.

// --- draw disc using either a macro or a function

#define C0(U,r,blur) smoothstep(r, r - blur, length(U) )

float C1(vec2 U, float r, float blur)
            { return smoothstep(r, r - blur, length(U) ); }



void mainImage( out vec4 O, vec2 U )
{
    U = ( U - .5*iResolution.xy) / iResolution.y; // normalize coords

    O = vec4(0);

    if (U.y>0.)  // top: use function

        for (float i = 0.; i < N; i++)
            O.r += C1( U, .03 * i, .003 * i);

    else         // bottom: use macro

#if 1
        for (float i = 0.; i < N; i++)
            O.g += C0( U, .03 * i, .003 * i);
#else
        for (float i = 0.; i < N; i++) {
            float r = .03 * i;
            O.g += C0( U, r, .003 * i);
        }
#endif
}
