#define iLodLimit 8

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float mixfactor = min(3.,length(vec2(uv.x-.25,uv.y-.4))*8.*(sin(iTime)+1.));

    vec3 color = texture( iChannel0, uv, 0. ).xyz;

    float j = 1.;
    for (int i = 1; i < iLodLimit; i++) {
        j *= .75;
    	vec3 lod = texture( iChannel0, uv, float(i) ).xyz;
    	color = mix(color,lod,mixfactor*j);

    }
	fragColor = vec4(color, 1.0 );
}
