const vec4 Bgnd = vec4(vec3(.8), 1);
const vec2 Size = vec2(500, 300);
const float Pi = 3.1415926535;

mat2 rot(float a) {
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

vec4 clampedTex(vec2 uv) {
    vec4 tex = vec4(0);
    if(all(greaterThan(step(abs(uv - .5), vec2(.5)), vec2(.5))))
    	tex = texture(iChannel0, vec2(uv.x, uv.y));
    tex.a = smoothstep(.1, .0, tex.g - 1.1 * max(tex.r, tex.b)) * tex.a;
    return tex;
}


vec4 drawOutline(vec2 uv) {
    vec4 tex = clampedTex(uv);
    vec3 color = vec3(0.);
    float dist = 10. / Size.x;

    if(tex.a < .3) {
    	int N = 8;
        vec4 outline = vec4(0);
        for(int i = 0; i < N; ++i) {
            vec2 dir = rot(float(i) * 2. * Pi / float(N)) * vec2(dist, 0);
            vec4 t = clampedTex(uv + dir);
            outline.rgb += color * t.a;
            outline.a += t.a;
        }
        tex = outline;
    }

    return tex;
}

vec4 drawSprite(vec2 uv) {
    vec4 tex = vec4(0);
    if(all(greaterThan(step(abs(uv - .5), vec2(.5)), vec2(.5))))
    	tex = texture(iChannel0, vec2(uv.x / 6.6, uv.y));
    return tex;
}


void addTexture(inout vec4 col, in vec4 sprite) {
    col = mix(col, vec4(sprite.rgb, 1.), sprite.a);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord - (iResolution.xy - Size) / 2.) / Size;
    vec4 col = Bgnd;
    addTexture(col, drawOutline(uv));
    //addTexture(col, drawSprite(uv) * vec4(vec3(1), .6));
    fragColor = col;
}
