const float PI = 3.1415926535;
const float TWO_PI = PI*2.0;

#define tex(uv) texture(iChannel0, uv)

float rand(vec2 s){ return fract(sin(dot(s.xy ,vec2(12.9898,78.233))) * 43758.5453); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 r  = 1.0/iResolution.xy;
    vec2 uv = fragCoord*r;
    vec2 d =  uv-0.5;
    float dd = dot(d, d);
    vec2 rouv = uv+(rand(uv+fract(iTime))*2.0-1.0)*dd*0.01;
    vec3 col = vec3(tex(rouv-d*0.05).r, tex(rouv-d*0.03).g, tex(rouv-d*0.01).b);
    float t = fract(iTime);
    float noise = rand(uv+t)*0.1;
    float sline = sin(uv.y*500.0+fract(iTime)*TWO_PI)*1.0+1.0;
    fragColor = vec4(col+noise*sline, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = texture(iChannel0, fragCoord.xy/iResolution.xy);
}

const float SATURATION = 10.0;

vec3 saturation(vec3 c, float s){
    const vec3 W = vec3(0.2125, 0.7154, 0.0721);
    vec3 t = vec3(dot(c, W));
    return mix(t, c, s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    fragColor = vec4(saturation(texture(iChannel0, fragCoord/iResolution.xy).rgb, SATURATION),1.0);
}

const int N = 2;
const float DARKNESS = 0.5;
const float STRIDE = 2.5;

const float K = 1.0/float((2*N+1)*(2*N+1));

float rand(vec2 s){ return fract(sin(dot(s.xy ,vec2(12.9898,78.233))) * 43758.5453); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 ir = 1.0/iResolution.xy;
    vec2 uv = fragCoord.xy*ir;
	vec3 c = texture(iChannel1, uv).rgb*DARKNESS;
    vec3 sum = vec3(0.0);
    for (int y = -N; y <= N; ++y){
        for (int x = -N; x <= N; ++x){
            sum += texture(iChannel0, uv+ir*vec2(x, y)*STRIDE).rgb;
        }
    }
    fragColor = vec4(mix(sum*K, c, 0.5), 1.0);
}
