vec2 fmod(vec2 x, vec2 y)
{
    return x - y * trunc(x/y);
}

float fmod(float x, float y)
{
    return x - y * trunc(x/y);
}

vec2 fmod(vec2 x, float y)
{
    return fmod(x, vec2(y,y));
}

vec2 uv0to1(vec2 pos)
{
    vec2 uvModified = mod(abs(pos),2.0);
    vec2 invert = floor(uvModified);
    uvModified = (invert*2.0 - uvModified) 
        * (-1.0*(1.0-invert)+1.0 *invert);
    return uvModified;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv0to1(uv*3.0 - 1.5);


    float amount = fmod(iTime * 0.25, 0.5);
    amount = iTime * 0.25;
    vec2 fromCenter = uv - 0.5;
    vec2 norm = fromCenter;
    norm = normalize(norm);
    fromCenter += norm + amount;
    fromCenter = fmod(fromCenter, 0.5);
    vec2 newPos = fromCenter * 2.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+vec3(0,2,4));
    //col = vec3(0.0,0.8,0.8);
	vec3 colb = 0.5 + 0.5*cos(vec3(0,2,4)+iTime+2.0);
    //colb = vec3(0.44, 0.04, 0.30);
    col *= max(newPos.x,0.0);
    colb *= max(newPos.y,0.0);
    vec3 res = col + colb;

    // Output to screen
    fragColor = vec4(newPos.x, 0.0, newPos.y, 1.0);
    fragColor = vec4(res.x,res.y,res.z,1.0);
    //fragColor = vec4(col.x,col.y,col.z,1.0);
    //fragColor = vec4(colb.x,colb.y,colb.z,1.0);
    //fragColor = vec4(0, 1.0, 1.0, 1.0);
}
