// NB: all (but last) should be called with clamp( expr, 0., 1. )
#define hueHSV(h)  ( -1. + abs( mod( 6. *(h) + vec4(0,4,2,0) , 6. ) - 3. ) )
#define hueCos0(h) (  .6 + .6 * cos( 6.3*(h) + vec4(0,23,21,0)  ) ) // https://www.shadertoy.com/view/ll2cDc
#define hueCos(h) 1.2*( .4 + .8*cos( 6.3*(h) + vec4(0,23,21,0)  ) ) // https://www.shadertoy.com/view/ll2cDc
#define hueIQ(h)   smoothstep( 0., 1., hueHSV(h) )                  // https://www.shadertoy.com/view/MsS3Wc

void mainImage( out vec4 O, vec2 u )
{
	vec2 R = iResolution.xy,
         U = u / R;
    float n = 3.*U.y;
    float x = fract(2.*U.x);

    // top to bottom: display different brands of hue 
	O = n>2. ? hueHSV(x) : n>1. ? hueIQ(x) : n>.5 ?  hueCos(x) : hueCos0(x);

  //if (n>.5) O = mix(vec4(1),clamp(O,0.,1.),.9); // desaturate all like hueCos0

    if (U.x>.5) O = pow(O, vec4(1./2.2) ); // sRGB correction on right

    if (int(u.x) == int(R.x/2.)  ) O-=O;   // grid
    if (int(u.y)%int(R.y/3.) == 0) O-=O;
}
