void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float f = texture(iChannel0, uv).x;
    f = smoothstep(0.04, 0.33, f*(f+0.2));
    fragColor = vec4(0.97, 0.0, 0.3, 0.0)*f;
}

#define T(x,y) vec4(texture(iChannel0, fract((fragCoord+vec2(x,y))/iResolution.xy)).xyz, 1)
#define F(i) (f(i))
#define BLUR (G(-5) + G(-4) + G(-3) + G(-2) + G(-1) + G(0) + G(1) + G(2) + G(3) + G(4) + G(5))

const float PI = atan(0., -1.);
float f(float i)
{
    return exp(-i*i/4.5);
}

// Horizontal blur

#define G(i) T(i, 0)*F(float(i))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 col = vec4(0.0);
    col += BLUR;
    col /= col.w;
    fragColor = col;
}

// Vertical blur

#define G(i) T(0, i)*F(float(i))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 col = vec4(0.0);
    col += BLUR;
    col /= col.w;
    fragColor = col;
}

// Transformation

mat2 rot(float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy-0.5;
    if (iFrame < 30 || texture(iChannel3, vec2(32./255., 0.0)).x > 0.5)
    {
        fragColor = texture(iChannel1, uv+0.5);
        return;
    }
    //uv = uv*rot(0.001)*0.99;
    float a = 0.3-cos(uv.y+iTime*0.2)*0.4;
    uv *= rot(-a);
    vec2 q = uv*vec2(0.99, 0.999) +
        	 vec2(cos(uv.y*3.1)*0.0000, sin(uv.x*32.0+iTime*0.8)*0.0015);
    uv = mix(q, uv, 0.4);
    uv *= rot(a);
    vec4 col = texture(iChannel0, fract(uv+0.5));
    fragColor = col;
}

// Sharpening

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy-0.5;
    vec4 col = T(-1, -1)*0.05 + T( 0, -1)*0.20 + T( 1, -1)*0.05 +
               T(-1,  0)*0.20 - T( 0,  0)*1.00 + T( 1,  0)*0.20 +
               T(-1,  1)*0.05 + T( 0,  1)*0.20 + T( 1,  1)*0.05;
    col /= 8.0;
    col = T(0, 0) - col*50.0;
    col -= 0.5;
    col = col*0.7;
    col = smoothstep(vec4(-0.5), vec4(0.5), col);
    col = mix(col, vec4(length(col.xyz)/sqrt(3.)), 0.01);
    float q = 0.06; //smoothstep(0.5, 1.0, texture(iChannel2, vec2(0.0, 1.0)).x)*0.7+0.05;
    if (iMouse.z > 0.0 && length(iMouse.xy-fragCoord.xy) < 20.0)
    {
        if (texture(iChannel3, vec2(90.0/256.0, 0.0)).x <= 0.5)
        	col.xyz = texture(iChannel3, vec2(88.0/256.0, 0.0)).x > 0.5 ? vec3(0) : vec3(1);
        else
            col.y = texture(iChannel3, vec2(88.0/256.0, 0.0)).x > 0.5 ? 0.0 : 1.0;
    }
    col.xyz -= cross(col.xyz, vec3(q));
    fragColor = col;
}
