float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}


float rectAA(vec2 position, vec2 widthHeight, float aaEdge, vec2 xYoffset) {
	vec2 rect = smoothstep(widthHeight, widthHeight + aaEdge, abs(position - xYoffset));
    float r = clamp(rect.x + rect.y, 0.0, 1.0);

    return 1.0 - r;
}


