void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0);
    col += texture(iChannel0,uv).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float Luminance(vec3 linearRgb)
{
    return dot(linearRgb, vec3(0.2126729f,  0.7151522f, 0.0721750f));
}

vec3 Prefilter(vec3 col,float t){
 	float l = Luminance(col);

    if(l > t)
        return col;
    return vec3(0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor.xyz = Prefilter(texture(iChannel1,uv).xyz,0.5);
    for(int i = 0;i < 6;i++)
    fragColor += texture(iChannel0,mix(uv,vec2(0.5 + (sin(uv.y * 50. + iTime * 5.0) * 0.25 + 0.25),0),0.01)) * 0.15 * vec4(1.0,0.8,0.1,1);

}
