void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // webcam pixel color
    vec3 col = texture(iChannel0, uv).rgb;

    // simple colorspace transforms
    mat3 rot = mat3(-0.3999643 ,  0.82848493, -0.39197103,
       0.74160659,  0.04123861, -0.6695663 ,
       0.53856125,  0.55849092,  0.63090386);
    mat3 rot_inv = mat3(-0.3999643 ,  0.74160659,  0.53856125,
       0.82848493,  0.04123861,  0.55849092,
       -0.39197103, -0.6695663 ,  0.63090386);
    vec3 cent = vec3(0.47968451, 
                     0.450743, 
                     0.45227517);

    vec3 rel = rot * (col - cent);

    rel.xy *= 0.5 * rel.xy * rel.xy; // damp these channels, but especially for values near
    // the color centroid
    rel.z *= 1.0 + 0.5 * step(0.0,rel.z); // bump up these values where above centroid
    vec3 new_col = (rot_inv * rel) + cent + vec3(0.15, 0.05, -0.1);
    // take inverse of color transform, add colorspace center back in, add yellow offset

    float weight = step(fragCoord.x, iMouse.x - 10.0);

    col = mix(new_col, col, weight);
    // Output to screen
    fragColor = vec4(col,1.0);
}
