void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 3.*(2.*fragCoord-iResolution.xy)/iResolution.y;
         u = -u.yx - vec2(1.,.0);
    vec2 v = u;
         u = u/dot(u,u);

    float e = -2.;
    float pi = 3.14159265359;

    for(float i = 0.; i < 7.; ++i)
    {
        u = cos(u.y-vec2(.0,.5*pi))*exp(u.x);
        u = cos(atan(u.y,u.x)*e-vec2(0.,pi*.5))*pow(length(u),e);
        u+= v;
    }
    fragColor = exp(dot(u,u)*-vec4(.03,.04,.05,.05));
}
