// simplifying bufA:"Day 107" ( 1480 chars )  by jeyko. https://shadertoy.com/view/3dByR1

// Ease from https://necessarydisorder.wordpress.com/
float E(float p) {
    return   p < 0.  ? 0.
           : p > .5  ? 1.
           : p > .25 ? p = 1.-p-p , 1. - 2.*p*p
           :           8.*p*p; 
}
void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, p,
         U = ( u - .5*R ) / R.y;

    float t = mod(iTime*.8, 3.5), r=1.57, x = U.x, z,m, i = 0.,
          A = E(t), B = E(t-1.), C = E(t-2.),
          b = r + .1*(A-C);

    U *= mat2( cos( r*( A+C + vec4(0,-1,1,0) ) ))
      * ( 1. - dot(U,U)*.05 );   
    U.x += .2* (A -B-B + C);

  //vec3 p = vec3(U,1) / dot(U,U);   
    z = dot(U,U); p = U/z;
    p.x -= sin( r* (A -B-B -C) ) *5.9;

    for(; i < 20.; i++) {
        p *= m = (2. - i/20. ) / clamp( dot(p,p), .1, 20. );
        z /= m;
        p = abs( mod(p.yx - b, b+b)  - b );
        if(i == 2.) p.x -= 1.1 + ( B+C - pow(A,.6)*2. ) /4.; 
    }

    t *= 1.8;
#define S(v) smoothstep(0.,.5/R.y, .0015 - length(v*z) )
    O =   S( p.x ) * vec4(1.9,.9,.6,0)   
        + S( min(p.y, p.x) )
          * pow( abs( sin( ( U.x + sin( (U.y-U.x)/.1 - t) ) /1.8) ), 6.)
          * max( 2.* sin( vec4(8,8,5,0) + sin(x + t) ) , 0. );
}
