// Haico ten Lohuis - Ayquo 2020
#define m mod(length(u-vec2(-sin(t),cos(t))*.2)
void mainImage( out vec4 c, in vec2 o ) {
    vec3 r=iResolution;                       
    float t=iTime+2.;                         
    vec2 u=o/r.xy-.5;                         
    u.y*=r.y/r.x;                             
    u.x+=sin(t+u.y*(cos(t)*8.+8.))*.1;        
    t*=2.;                                    
    vec3 q=vec3(1,.8,.9)*m-t*.2,.05)*20.;     
    t/=.8;                                    
    c=vec4(1.-m,.05)*20.>.5?vec3(1.-q.x):q,1);}
