// Author: bitless
// Title: Rectangular pattern
// Thanks to Patricio Gonzalez Vivo & Jen Lowe for "The Book of Shaders"
// and Fabrice Neyret (FabriceNeyret2) for https://shadertoyunofficial.wordpress.com/
// and Inigo Quilez (iq) for  https://iquilezles.org/www/index.htm
// and whole Shadertoy community for inspiration.

#define p(t, a, b, c, d) ( a + b*cos( 6.28318*(c*t+d) ) )
#define W .02

void mainImage( out vec4 f, in vec2 g )
{
    vec2 r = iResolution.xy
        ,u = (g+g-r)/r.y/2.
        ,l = abs(fract(u*10.)-.5);

    float t = sin(sin(u.x + sin(.5*u.y + iTime)) +
              sin(length(u)*1.1+iTime) +
              sin(u.x*.9+iTime))*.5+.5
        ,m = smoothstep(W+12./r.y, W ,min(abs(l.x-(l.y*t)),abs(l.y-(l.x*t))));
    f = vec4(p(t,.7,.5,.5,vec3(.1,.2,.3))*m,1.);
}
