void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(uv.x * sin(iTime*0.5*uv.y) * 10.25 - 3.,
                    uv.y * exp(iTime*0.5*uv.x) * 50.25 - 10.,
                    uv.xy * exp(iTime* 0.5*uv.xy) * 20.25 - 8.);

    float s = fract(sin(uv.x * uv.y - 1.0)*10000000.);

    col = col * vec3(sin(s*iTime*0.3)* 10., sin(s*iTime*0.6)* 10., sin(s*iTime*1.)* 10.);
    // Output to screen
    fragColor = vec4(col,1.0);
}
