// color version of https://shadertoy.com/view/wslcRn

#define blue(U) texelFetch(iChannel1, ivec2((U-.5)/n*float(S)) % textureSize(iChannel1,0).xy, 0).x

#define N 2
float n = float(N);

void mainImage( out vec4 O, vec2 U )
{
    float r = T0(0).a,
          s = r/R.y; int S = int(.5+1200./R.y);

    vec4  C = T0( floor(U/n)*n * s ),
          L = pow(C,vec4(4.));
    float v = ( 2.*L.r-L.g ) / 2.1, 
          l = 0.;
    // v = pow(1.5*v,4.);
    for (int k=0; k<S*S; k++) {
        vec2 P = vec2(k%S,k/S) - float(S-1)/2.;
        l += step( blue(U + P/float(S)) , v/2. );
    }
    l /= float(S*S);
  //l = pow(l, 1./2.2);
    O = vec4(l);

    O *= 2.*C; // comment for B&W, uncomment for color
  //O *= mix(vec4(1),2.*C,.3);  // variant: just a touch of colors
}

// --- select a target frame in video

void mainImage( out vec4 O, vec2 U )
{
    if ( iChannelTime[1] < 4.51 )  // 3.93
         O = T1(U),
         O.a = R.y; // memorize the size
    else O = T0(U); 
}
