// Inigo Quilez 2019
//
// Comparison between triangular noise, and a 
// triangularized-blue-noise, for dithering.

const int bands = 10;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;

    // color is a horizontal gradient    
    vec3 col = p.xxx;

    // dither test
    if( abs(p.y-0.5)<0.25 )
    {
        float applyDither = step( 0.25, fract(iTime/4.0) );
        ivec2 off = ivec2(iFrame*13,iFrame*311) & 1023;

        // fetch uniform or blur noise        
        vec2 no = (p.y<0.5) ? texelFetch( iChannel0, (off+ivec2(fragCoord) & 1023), 0 ).xy :
                              texelFetch( iChannel1, (off+ivec2(fragCoord) &  255), 0 ).xy;
        // triangular noise
        float noise = 1.0-no.x-no.y;
        // apply dither
        col += applyDither*noise/float(bands+1);
        // quantizer
        col = round(col*float(bands))/float(bands);
    }

    //draw borders
    if( int(fragCoord.y) % int(iResolution.y/4.0) == 0)
    	col = vec3(1.0, 0.0, 0.0);

    fragColor = vec4( col, 1.0 );
}
