float random(vec2 uv)
{
	return fract(sin(dot(uv.xy, vec2(12.9898, 78.233))) * 43758.5453123);    
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord/iResolution.y)-(iResolution.xy/iResolution.y*.5))*2.;
    uv *= sin(iTime*2.)*10. + 12.;
    float angle = iTime * .5;
    float c = cos(angle);
    float s = sin(angle);
    uv *= mat2( c, -s, s, c);
    uv += vec2(iTime)*6.;

	vec2 gv = (fract(uv)-.5)*2.;
    vec2 id = vec2(int(uv.x), int(uv.y));

    vec3 col = vec3(0);

   	gv.x *= -(step(.5, random(id))-.5)*2.;

    float aa = (sin(iTime*2.)*10. + 12.)/iResolution.y;
    float dist = distance(vec2(1), gv);
    if( dist < 1.1 && dist > 0.9)
  	{
    	col = vec3( smoothstep( 0.1+aa, 0.1-aa, abs(dist - 1. ) ) );
   	}

    dist = distance(vec2(-1), gv);
    if(dist < 1.1 && dist > 0.9)
    {
        col = vec3( smoothstep( 0.1+aa, 0.1-aa, abs(dist - 1. ) ) );
    }

    fragColor = vec4(col,1.0);
}
