#define saturate(x) clamp(x, 0.0, 1.0)

const vec3  Color 	 = vec3(0.8, 1.0, 0.95);
const float Contrast = 0.7;
const float Power    = 0.8;

#define GRADIENT_MAP1	0
#define GRADIENT_MAP2	1

#define GRADIENT_MAP 	GRADIENT_MAP1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = fragCoord/iResolution.xy;  
    uv += vec2(0.9); //Panning

    vec2 displaceUV = uv * 0.5;
    vec2 displacement = texture(iChannel3, displaceUV).rg;

    vec2 gradientMapUV;
   	gradientMapUV.x = pow(saturate(mix(0.5, texture(iChannel0, uv + displacement).x, Contrast)), Power);
    gradientMapUV.y = 0.0;

    #if GRADIENT_MAP == GRADIENT_MAP1
    vec3 gradientMap = texture(iChannel1, gradientMapUV).rgb;
    #else
    vec3 gradientMap = texture(iChannel2, gradientMapUV).rgb;
    #endif

    vec3 finalCol = Color * gradientMap;
    fragColor = vec4(finalCol, 1.0);
}
