bool rect(vec2 bl, vec2 tr, vec2 uv) {
    vec2 pos = smoothstep(bl, tr, uv);
    return all(lessThan(pos,vec2(1))) && all(greaterThan(pos, vec2(0)));
}

#define RECT_COUNT 64.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = 0.25*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 addcol = vec3(0.15, 0, 0);
    for (float i = 0.; i < RECT_COUNT; i++) {
        vec2 bl = vec2(sin(iTime*((i/RECT_COUNT)+1.)) + cos(i*3.14*24.), cos(iTime*((i/RECT_COUNT)+1.) + sin(i*3.14*24.)));
        vec2 tr = vec2(sin(iTime*((i/RECT_COUNT)+1.4)) + cos(i*3.14*14.), cos(iTime*((i/RECT_COUNT)+1.4) + cos(i*3.14*34.)));
        bl = bl*vec2(0.25, 0.5) + 0.5;
        tr = tr*vec2(0.25, 0.5) + 0.5;
        col += rect(bl, tr, uv)? addcol: vec3(0);
        addcol = addcol.gbr;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
