float circle(vec2 uv, vec2 ctr, float ry, float sz) {
	float pct = 0.;
    pct = distance(uv, ctr);
    pct = smoothstep(sz + 3./ry, sz, pct);
    return pct;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 R = iResolution.xy;
    vec2 uv = (2. * fragCoord - R) / R.y;
	vec3 col = vec3(0.);
    float mgn = 0.45;
    float sz = 0.5;
    vec3 wh = vec3(1.);

    vec2 lctr = vec2(
     min(abs(sin(t)+0.5),1.-mgn),
     min(abs(sin(t)-0.5),1.-mgn)
    );

    vec2 rctr = vec2(
     min(abs(sin(t)-0.5),1.-mgn),
     min(abs(sin(t)+0.5),1.-mgn)
    );

    vec2 cctr = vec2(
     max(abs(cos(t)-sz),mgn),
     max(abs(cos(t)-(0.5-sz)),mgn)
    );

    vec2 ictr = vec2(
     max(abs(cos(t)+sz),mgn),
     max(abs(cos(t)+(0.5-sz)),mgn)
    );

    col += circle(uv, lctr, R.y, sz) * vec3(0., 0., 1.);
    col += circle(uv, rctr, R.y, sz) * vec3(0., 1., 0.);
    col += circle(uv, cctr, R.y, sz) * vec3(1., 0., 0.);
    col += circle(uv, ictr, R.y, sz) * wh;

    fragColor = vec4(col,1.0);
}
