float saturate(float x)
{
    return clamp(x, 0.0, 1.0);
}

vec3 saturate(vec3 x)
{
    return clamp(x, vec3(0.0), vec3(1.0));
}

vec2 saturate(vec2 x)
{
    return clamp(x, vec2(0.0), vec2(1.0));
}
float sawTooth(float t) 
{
    return cos(t+cos(t))+sin(2.*t)*.2+sin(4.*t)*.02;
}

//nv general settings
#define NV_BRIGHTNESS 2.5
#define NV_COLOR      vec3(1.0, 0.40, 0.53) //can be higher than 1 bcuz i saturated it
#define NV_BLURRING vec3(0.001,0.002,0.003)

//effect settings
#define FLICKERING_INTENSITY 0.0015
#define FLICKERING_FREQ 50.0
#define NOISE_INTENSITY 0.15
#define SCANLINES_INTENSITY 0.025



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 image = texture( iChannel0, uv);

     //cheap noise
    float noise  = fract(sin(dot(uv, vec2(12.0, 78.0) + (iTime*3.17f) )) * 43758.0); 
    image += noise*NOISE_INTENSITY;

    //just scanlines
    image += SCANLINES_INTENSITY*sawTooth((uv.y*(iTime*iTime))*iResolution.y);

    //image flickering
    image += FLICKERING_INTENSITY * sin(iTime*FLICKERING_FREQ);  

    //fast CA, and multipling image for better brightness
    image += texture( iChannel0, uv + NV_BLURRING.x)*NV_BRIGHTNESS;
    image += texture( iChannel0, uv + NV_BLURRING.y)*NV_BRIGHTNESS;
    image += texture( iChannel0, uv + NV_BLURRING.z)*NV_BRIGHTNESS;


    //fast color remover
    image.g = ((image.r + image.g + image.b)/3.f)*saturate(NV_COLOR.x);
    image.r = ((image.r + image.g + image.b)/3.f)*saturate(NV_COLOR.y);
    image.b = ((image.r + image.g + image.b)/3.f)*saturate(NV_COLOR.z);



    fragColor = image;

}
