//Constants

#define PI 		3.14159265359
#define TWO_PI  6.28318530718

//Simple defines so I can write HLSL in GLSL

#define float2 	vec2 
#define float3 	vec3 
#define float4 	vec4
#define mix 	lerp
#define frac	fract

//Main function for Unreal Engine.

float4 radial_progress(float2 UV, float VAL)
{   

    //START UE4 CUSTOM EXP

    float 	SMOOTH	= 0.005;
    float2 	uv 		= (UV*2.0)-1.0;
    float2  origin  = float2(0.0, 0.0);
    uv.x /= 9.0/16.0; //remove this line in UE (compensating for 16:9 on ShaderToy)

    float 	ir 		= 0.75;
    float 	or 		= 0.95;
    float 	d  		= length(uv);
    float 	ring  	= smoothstep(or+SMOOTH, or-SMOOTH, d) - smoothstep(ir+SMOOTH, ir-SMOOTH, d);   
    float 	a 		= atan(uv.y-origin.y, uv.x-origin.x);  
   	float	theta 	= (a < 0.0) ? (a + TWO_PI) / TWO_PI : a / TWO_PI;
    float	bar 	= step(theta, VAL);
    float	ui 		= ring * bar;

    float4 	colour 	= float4(ui, ui, ui, ui);

	return 	colour;

	//END UE4 CUSTOM EXP
}


//ShaderToy Main Function

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =  fragCoord.xy / iResolution.xy;
	vec2 pos = iMouse.xy / iResolution.xy;

    fragColor = radial_progress(uv, 0.25);
}
