void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;

    uv.x *= iResolution.x/iResolution.y;

    uv *= 5.;
    uv.x += sin(iTime*0.1)*5.;
    uv.y += cos(iTime*0.2332)*5.;

    uv *= cos(uv+(0.3*iTime))+sin(iTime+uv)+2.;


    vec2 grid = floor(uv*4.);
    grid *= .1;


    float dist = length(uv+(grid.x*grid.y));//length(mod(uv, 1.)-.5)+sin(grid.x*iTime*3.)+cos(grid.y*iTime);

    // Output to screen
    fragColor = vec4(sin(iTime),cos(sin(dist*6.)*fract(iTime+dist))-.3,sin(dist*fract(dist+iTime*.3)*.3),1.0);

}
