void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // where 0.0 is the center
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    float brightness = min(-step(0.6, abs(uv.x)) + 1.0, -step(0.2, abs(uv.y)) + 1.0);
    //float brightness = -step(0.7, abs(uv.y)) + 1.0;
    //float brightness = -step(0.2, abs(uv.y)) + 1.0;
    vec3 col = vec3(brightness);

    // Output to screen
    fragColor = vec4(col,1.0);
}
