void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uvs that are centered and scaled:
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.x;
    uv = (uv*25.0)-0.5;

    // individual cell uvs:
    vec2 cells = fract(uv)-0.5;

    // a number to offset animation for each cell with:
    float cell_index = length((uv+0.5)-cells)*-1.;

    // animated time:
    float cell_time = sin(iTime*1.0+cell_index*0.08);
    // make time stand still for a bit:
    cell_time = smoothstep(-0.5,0.5, cell_time);

    // vector that spins around:
    vec2 spinning = 0.25*vec2(sin(cell_time*10.0), cos(cell_time*10.0));

    // round shape thing that's moving around by "spinning" vector.
    float blob = smoothstep(0.15+0.1*cell_time, 0.1+0.1*cell_time, length(cells + spinning));

    // the end!
    fragColor = vec4(vec3(blob), 1.0);
}
