void mainImage( out vec4 Q, in vec2 U )
{
	Q.xy = vec2(0);
    for (float i = 0.; i < R.y; i++)
        Q.xy += D(vec2(U.x,i)).xy*e(-6.28318530718*(U.y-0.5-0.5*R.y)/R.y*i);

