#define D(U) texelFetch(iChannel3,ivec2(U),0)

vec2 cmul (vec2 a,float b) { return mat2(a,-a.y,a.x)*vec2(cos(b), sin(b)); } 
vec2 cmul (vec2 a,vec2  b) { return mat2(a,-a.y,a.x)*b; } 

vec2 pol (vec2 a) {return vec2(length(a),atan(a.y,a.x));}
vec2 car (vec2 a) {return a.x*vec2(cos(a.y),sin(a.y));}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = vec4(0);
    vec2 C = -6.28318530718*(U-.5-0.5*R)/R;
    for (float i = 0.; i < R.x; i++)
        Q.xy += cmul(A(vec2(i,U.y)).xy,C.x*i);

    if (length(C)>5.) Q*= 0.;
    Q.xy *= sin(10.*length(C)*10.*sin(.3*iTime));

}

