#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 pv = vec2(length(uv - vec2(0.5)), atan(uv.y - 0.5, uv.x - 0.5));
    vec3 text = texture(iChannel0, uv).rgb;
    float t = sin(iTime);

    float var = 0.08 * (sin(0.2/t * pv.x + pv.x * t) + sin(0.4/t * -pv.x - pv.x * t));
    pv.y += var;


    uv = vec2(pv.x * cos(pv.y), pv.x * sin(pv.y)) + 0.5;
    vec3 col = vec3(0);

    vec3 effect = texture(iChannel0, uv).rgb - text;
    vec3 bw = smoothstep(0.0, 1.0, text.rgb);
    col = text;


   	col.r += effect.r*4.*smoothstep(0.0, 0.015, var);


    fragColor = vec4(col,1.0);
}
