float saturate(float x)
{
  return max(0., min(1., x));
}
float outl (vec2 st,vec2 dims) {
    st = vec2(length(st.x),length(st.y));
    return (step(st.x,dims.x)*step(st.y,dims.x))*1.-(step(st.x,dims.y)*step(st.y,dims.y));
}
float cros (vec2 st,float w) {
    st = vec2(length(st.x),length(st.y));
    return saturate(step(st.x,w)+step(st.y,w));
}
vec2 rotate(vec2 uv, float rotation)
{
    return vec2(cos(rotation) * uv.x + sin(rotation) * uv.y, cos(rotation) * uv.y - sin(rotation) * uv.x);
}

#define Deg2Rad 0.017453292519943278
#define COLOR vec3(1.0,0.949,0.01176)
#define SCALE 1.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (gl_FragCoord.xy/iResolution.xy)-0.5;
    uv *= vec2((iResolution.x / iResolution.y)*SCALE, SCALE);
   	vec2 uvr = rotate((uv*1.0),45.*Deg2Rad);
    float b1 = outl(uvr, vec2(0.45,0.33));
    float b2 = outl(uvr, vec2(0.25,0.13));
    float c1 = 1.-cros(uv,0.03);
    float msk = (1.-step(abs(uvr.x),0.26))*step(uv.x,0.0)*step(uv.y,0.0);
    msk += step(abs(uvr.x),0.3)*step(0.,uv.x)*step(0.,uv.y);
    vec3 col = (vec3(b1+b2)*c1*1.-msk)*COLOR;
    fragColor = vec4(col,1.0);
}
