#define PIXELS 5.

vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 c = floor(uv * PIXELS) / PIXELS;
    vec2 t = (uv - c) * PIXELS - 0.5;

    if (t.x + t.y < 0.) {
        if (t.x - t.y < 0.) {
            uv = vec2(0.25, 0.5);
        } else {
            uv = vec2(0.5, 0.25);
        }
    } else {
        if (t.x - t.y < 0.) {
            uv = vec2(0.5, 0.75);
        } else {
            uv = vec2(0.75, 0.5);
        }
    }

    // borders
    float a = 1.0;
    a *= min(pow(abs(t.x + t.y) ,.3)*2., 1.2);
    a *= min(pow(abs(t.x - t.y) ,.3)*2., 1.2);
    a *= min(pow(    t.x + 0.5 ,.25)*2., 1.);
    a *= min(pow(abs(t.x - 0.5),.25)*2., 1.);
    a *= min(pow(    t.y + 0.5 ,.3)*2., 1.);
    a *= min(pow(abs(t.y - 0.5),.3)*2., 1.);

    a *= abs(t.x + t.y) < 0.005 || abs(t.x - t.y) < 0.005 ? 0. : 1.;
    a *= abs(t.x) > 0.498 || abs(t.y) > 0.495 ? 0. : 1.;

    uv = c + uv / PIXELS;

    vec3 col = texture(iChannel0, uv).rgb;
    col = rgb2hsv(col);
    col.y = pow(col.y, .33);
    col.x = mix(col.x, (0. + floor(col.x * 2.)/2.) / 1., 0.85);
    col = hsv2rgb(col);

    // Output to screen
    fragColor = vec4(col * a, 1.);
}
